export class CatalystError extends Error {
    constructor(msg, code = 1, originalError) {
        super(msg);
        this.message = msg;
        this.code = code;
        this.originalError = originalError;
    }
    toJSON() {
        return {
            message: this.message,
            code: this.code
        };
    }
    toString() {
        return JSON.stringify(this.toJSON());
    }
}
