// eslint-disable-next-line @typescript-eslint/no-extraneous-class
export class Handler {
    static registerHandler(handlerType, name, callback, res) {
        // first time registering a handlerType : eg. Bot
        if (Handler.handler[handlerType] === undefined) {
            Handler.handler[handlerType] = {
                name: { fun: callback, res }
            };
        }
        Handler.handler[handlerType][name] = { fun: callback, res };
    }
}
Handler.handler = {};
